// Load a Google Font on-demand (requires internet connection)
export function loadGoogleFont(family: string, weight: number = 400, italic: boolean = false): void {
  const variant = italic ? `ital,wght@1,${weight}` : `wght@${weight}`;
  const link = document.createElement('link');
  link.href = `https://fonts.googleapis.com/css2?family=${family.replace(/ /g, '+')}:${variant}&display=swap`;
  link.rel = 'stylesheet';
  link.crossOrigin = 'anonymous';
  document.head.appendChild(link);
}

// Parse variant string to get numeric weight
export function parseVariantWeight(variant: string): number {
  if (variant === 'regular' || variant === 'italic') return 400;
  const match = variant.match(/^(\d+)/);
  return match ? parseInt(match[1], 10) : 400;
}

// Check if variant is italic
export function isVariantItalic(variant: string): boolean {
  return variant.includes('italic');
}
